mapper.select:
select id id , seq seq , status status , op_user_id opUserId , op_time opTime , dict_key dictKey , sort sort , remark
remark ,
dict_value dictValue from sys_dictionary t
where 1=1
 and t.seq = #{seq } 
 and t.status = #{status } 
 and t.op_user_id = #{opUserId } 
 and t.op_time = #{opTime } 
 and t.dict_key like #{dictKey } 
 and t.sort like #{sort } 
 and t.id = #{id } 
 and t.remark = #{remark } 
 and t.dict_value = #{dictValue } 
order by t.sort,t.seq

mapper.update:
update sys_dictionary t
t.seq=#{seq}, t.status=#{status}, t.op_user_id=#{opUserId}, t.op_time=#{opTime}, t.dict_key=#{dictKey}, t.sort=#{sort}, t.id=#{id}, t.remark=#{remark}, t.dict_value=#{dictValue},
where t.id = #{id}

mapper.delete:
delete from sys_dictionary t where
t.id = #{id} in

	#{id}
